//import React from 'react';
import React, {FunctionComponent, useState, useEffect} from 'react';
import POKEMONS from './models/mock-pokemon';
import Pokemon from './models/pokemon';

const App: /*React.FC */ FunctionComponent = () => {
    /* le hook permettant de rajouter le state à un composant de fonction
    (stateless component) est useState */
 
    //const name: String = 'React';
    //const [name, setName] = useState<String>('React'); // les <> servent à typer le retour afin de s'assurer qu'on reçoit la bonne donnée
  
  const [pokemons, setPokemons] = useState<Pokemon[]>([]);  
   useEffect(() => {
     setPokemons(POKEMONS);
    }, []);


 return (
  
  <div>
    <table>
    {pokemons.map((pokemon) => (
          <div>
              <thead>{pokemon.name}
            <tr>
              <th key={pokemon.name}>{pokemon.name}</th>
            </tr>
          </thead>
          <tbody>
            <tr>
                <td key={pokemon.picture}><img src={pokemon.picture} alt="un pokemon"/></td>

                annee: <tr key={pokemon.created.getFullYear()}>{pokemon.created.getFullYear()}</tr>
               id:  <tr key={pokemon.id}>{pokemon.id}</tr>
               hp: <tr key={pokemon.hp}>{pokemon.hp}</tr>
               cp:  <tr key={pokemon.cp}>{pokemon.cp}</tr>
            </tr>
          </tbody>
          <br/>
          </div>
          
          
        
        ))}
    </table>
</div>
 
 
 )
}

export default App; 

